mapper.update:
UPDATE event
SET    applier_cnt = applier_cnt + 1
WHERE  event_id = #{eventId}

UPDATE applier
SET success = 'o'
WHERE applier_id = #{applierId}

UPDATE applier
SET success = 'x'
WHERE applier_id = #{applierId}

mapper.select:
SELECT count(*)
FROM applier
WHERE user_id = #{userId} AND event_id=#{eventId}
