mapper.delete:
		DELETE FROM users
		WHERE  id = #{id}
    	   AND pw = #{pw}

mapper.update:
	UPDATE  users 
	SET        pw = #{pw},
            phone = #{phone},
            email = #{email},
        like_area = #{likeArea}
	WHERE  user_id = #{userId}

mapper.select:
SELECT truck_doc truckDoc
FROM users
WHERE user_id = #{userId}
